import React from "react";
import {Card, Skeleton} from "@nextui-org/react";

const Shimmer = () => {
  
 return (
    <div>
      <Card className="w-[300px] h-[300px] space-y-5 p-4" radius="lg">
        <Skeleton  className="rounded-lg">
          <div className="h-24 rounded-lg bg-gray-400"></div>
        </Skeleton>
        <div className="space-y-3">
          <Skeleton  className="w-3/5 rounded-lg">
            <div className="h-3 w-full rounded-lg bg-gray-500"></div>
          </Skeleton>
          <Skeleton  className="w-4/5 rounded-lg">
            <div className="h-3 w-full rounded-lg bg-gray-300"></div>
          </Skeleton>
          <Skeleton  className="w-2/5 rounded-lg">
            <div className="h-3 w-full rounded-lg bg-gray-400"></div>
          </Skeleton>
        </div>
      </Card>
    </div>
  );
}

export default Shimmer;
