import React from "react";
import { ReactDOM } from "react";

const Header = () =>{
  return(
      <div className="header">
        <div className="logo-container">
          <img className="logo" src="https://img.freepik.com/free-vector/food-shopping-logo-template-design_460848-10299.jpg?w=900&t=st=1705915902~exp=1705916502~hmac=0ea84015c19aec7159c10ea9c337ec0f01617f6882edf006ae03888751254293" alt="logo" />
        </div>
        <div className="nav-items">
          <ul>
            <li>Home</li>
            <li>About Us</li>
            <li>Contact Us</li>
            <li>Cart</li>
          </ul>
        </div>
      </div>
  );
};

const RestaurantCart = (props) =>{
 return(
  <div className="res-card" style={{backgroundColor:"#f0f0f0"}}>
    <img className="res-img" src={props.imgSrc} alt="sayaji" /> 
    <h3>{props.resName}</h3>
    <h4>{props.cuisine}</h4>
    <h4>{props.rating}</h4>
    <h4>{props.DeliveryTime}</h4>
  </div>
 );
};

const Body = () =>{
  return(
    <div className="body">
      <div className="search">Search</div>
      <div className="res-container">
        <RestaurantCart imgSrc="https://imgcy.trivago.com/c_fill,d_dummy.jpeg,e_sharpen:60,f_auto,h_627,q_auto,w_1200/itemimages/10/12/1012165_v4.jpeg" resName="Sayaji" cuisine="Luxury Dine-in" rating="4.5 star" DeliveryTime="30 min" />
        <RestaurantCart altname="kfc" imgSrc="" resName="KFC" cuisine="Burgers, Biryani" rating="4.2 star" DeliveryTime="27 min" />
        <RestaurantCart altname="marriot" imgSrc="" resName="Marriot" cuisine="Indian, Chinese" rating="4.7 star" DeliveryTime="28 min" />
      </div>
    </div>
  );
};

const AppLayout = () =>{
  return(
     <div className="app">
       <Header />
       <Body />
     </div>
  );
};

const App = () =>{
  return(
  <AppLayout />
  );
};

export default App;
