import { useEffect } from "react";
import { useState } from "react";
import Shimmer from "./components/Shimmer";

const RestaurantMenu = () =>{
   const[resInfo, setResInfo] = useState(null); 

   useEffect(()=>{
       fetchMenu(); 
   }, []);

   const fetchMenu = async () =>{
    const data = await fetch("https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=22.7611352&lng=75.8750008&restaurantId=65132&catalog_qa=undefined&submitAction=ENTER")

    const json = await data.json();
    console.log(json);
    setResInfo(json.data);
   } ;
  
  if( resInfo === null ) return <Shimmer />;
  
  const {name, cuisines, costForTwoMessage} = resInfo?.cards[0]?.card?.card?.info;
  
  const {itemCards} = resInfo?.cards[2]?.groupedCard?.cardGroupMap?.REGULAR?.cards[2].card.card;

  return (
    <div className="menu">
     <h1>{name}</h1>
     <h3>{cuisines.join(", ")}</h3>
     <h3>{costForTwoMessage}</h3>
     <ul>
        {<li> itemCards.map((item) =>  item.card.info.name ) </li>}
     </ul>
     </div>
  );
};

export default RestaurantMenu;
